mapper.insert:

	select seq_pid.nextval from dual

insert into product(
	pid, pname, pprice, pimage, pinfo, pdetail, pcatno, pattachoname, pattachsname, pattachtype, pattachdata
) values(
	#{pid}, #{pname}, #{pprice}, #{pimage}, #{pinfo}, #{pdetail}, #{pcatno}, #{pattachoname}, #{pattachtype}, #{pattachdata}
)
