mapper.insert:

	select seq_stid.nextval from dual

insert into styles(
stid, stpnum, stuserid,
stattachoname,stattachsname,stattachtype,stattachdata
) values(
#{stid}, #{stpnum},
#{stuserid}, #{stattachoname}, #{stattachsname},
#{stattachtype},#{stattachdata}
)

mapper.select:

select rnum, stid, stpnum, stuserid,
stattachoname,stattachsname,stattachtype,stattachdata
from (
    select rownum as rnum, stid, stpnum, stuserid, stattachoname,stattachsname,stattachtype,stattachdata
    from (
        select stid, stpnum, stuserid, stattachoname,stattachsname,stattachtype,stattachdata
        from styles
        order by stid desc
    )
    where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}
